import React, { useEffect, useState } from "react";

const ListingDashboard = () => {
  const [listings, setListings] = useState([]);
  const [editingId, setEditingId] = useState("");
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    price: "",
    images: "",
    location: "",
    agent_email: "",
  });

  const handleEdit = (property) => {
    setEditingId(property.id);
    setFormData({
      title: property.title,
      description: property.description,
      price: property.price,
      images: Array.isArray(property.images)
      ? property.images.join(", ")
      : property.images,
      location: property.location,
      agent_contact: property.agent_contact, // match property key
    });
  };

  const handleUpdate = async (e, id) => {
    e.preventDefault();

    try {
      await fetch(`/api/updatelistings/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      setEditingId(null);
      fetchListing(); // refresh the table
    } catch (error) {
      console.error("Update failed", error);
    }
  };

  const fetchListing = () => {
    fetch("/api/displaylistings")
      .then((res) => res.json())

      .then((data) => setListings(data))

      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchListing();
  }, []);

  const handleDelete = async (id) => {
    await fetch(`/api/deletelistings/${id}`, { method: "DELETE" });
    fetchListing();
  };

  return (
    <div className="flex h-screen w-full justify-center items-start">
      <div className="overflow-auto">
        <table className="min-w-full border-collapse border border-gray-300 bg-white rounded-md shadow">
          <thead className="bg-blue-100 text-gray-700">
            <tr className="">
              <th className="px-14 py-2 text-center border-b">
                Property Title
              </th>
              <th className="px-14 py-2 text-center border-b">Description</th>
              <th className="px-14 py-2 text-center border-b">Price</th>
              <th className="px-14 py-2 text-center border-b">Images</th>
              <th className="px-14 py-2 text-center border-b">Location</th>
              <th className="px-14 py-2 text-center border-b">Agent Email</th>
              <th className="px-14 py-2 text-center border-b"></th>
            </tr>
          </thead>

          <tbody>
            {listings.map((property, idx) => (
              <React.Fragment key={property.id}>
                <tr
                  className={
                    idx % 2 === 0
                      ? "bg-white border-b text-center"
                      : "bg-gray-50 border-b text-center"
                  }
                >
                  <td className="px-10 border-r-2 border-gray-300 py-2 border-b">
                    {property.title}
                  </td>
                  <td className="px-10 py-2 border-r-2 border-b">
                    {property.description}
                  </td>
                  <td className="px-10 py-2 border-r-2 border-b">
                    {property.price}
                  </td>
                  <td className="px-10 py-2 border-r-2 border-b">
                    {Array.isArray(property.image_urls || property.images)
                      ? (property.image_urls || property.images).map(
                          (url, i) => (
                            <img
                              key={i}
                              src={url}
                              alt="property"
                              className="w-20 h-20 object-cover mb-1"
                            />
                          )
                        )
                      : property.images}
                  </td>
                  <td className="px-10 py-2 border-r-2 border-b">
                    {property.location}
                  </td>
                  <td className="px-10 py-2 border-r-2 border-b">
                    {property.agent_contact}
                  </td>
                  <td className="flex flex-col gap-4 px-10 border-r-2 items-center justify-center border-b">
                    {/* Edit Icon */}
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                      strokeWidth="1.5"
                      stroke="currentColor"
                      className="size-6 cursor-pointer"
                      onClick={() => handleEdit(property)} // âœ… fixed
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        d="m16.862 4.487 1.687-1.688a1.875 1.875 0 1 1 2.652 2.652L10.582 16.07a4.5 4.5 0 0 1-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 0 1 1.13-1.897l8.932-8.931Zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0 1 15.75 21H5.25A2.25 2.25 0 0 1 3 18.75V8.25A2.25 2.25 0 0 1 5.25 6H10"
                      />
                    </svg>

                    {/* Delete Icon */}
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                      strokeWidth="1.5"
                      stroke="currentColor"
                      className="size-6 hover:text-red-500 cursor-pointer"
                      onClick={() => handleDelete(property.id)}
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        d="m14.74 9-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 0 1-2.244 2.077H8.084a2.25 2.25 0 0 1-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 0 0-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 0 1 3.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 0 0-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 0 0-7.5 0"
                      />
                    </svg>
                  </td>
                </tr>

                {/* Edit Form Row */}
                {editingId === property.id && (
                  <div className="absolute z-50 inset-0 flex items-center justify-center backdrop-blur-sm bg-black bg-opacity-40">
                    <div className="w-1/2 h-auto bg-white p-6 shadow-lg rounded-md">
                      <form
                        onSubmit={(e) => {
                          e.preventDefault();
                          handleUpdate(e, property.id);
                        }}
                        className="flex flex-col gap-4"
                      >
                        <input
                          value={formData.title}
                          onChange={(e) =>
                            setFormData({ ...formData, title: e.target.value })
                          }
                          placeholder="Title"
                          className="w-full border px-3 py-2"
                        />
                        <input
                          value={formData.description}
                          onChange={(e) =>
                            setFormData({
                              ...formData,
                              description: e.target.value,
                            })
                          }
                          placeholder="Description"
                          className="w-full border px-3 py-2"
                        />
                        <input
                          value={formData.price}
                          onChange={(e) =>
                            setFormData({ ...formData, price: e.target.value })
                          }
                          placeholder="Price"
                          className="w-full border px-3 py-2"
                        />
                        <input
                          value={formData.images}
                          onChange={(e) =>
                            setFormData({ ...formData, images: e.target.value })
                          }
                          placeholder="Images"
                          className="w-full border px-3 py-2"
                        />
                        <input
                          value={formData.location}
                          onChange={(e) =>
                            setFormData({
                              ...formData,
                              location: e.target.value,
                            })
                          }
                          placeholder="Location"
                          className="w-full border px-3 py-2"
                        />
                        <input
                          value={formData.agent_contact}
                          onChange={(e) =>
                            setFormData({
                              ...formData,
                              agent_contact: e.target.value,
                            })
                          }
                          placeholder="Agent Contact"
                          className="w-full border px-3 py-2"
                        />
                        <div className="flex gap-4 mt-4 justify-end">
                          <button
                            type="submit"
                            className="bg-blue-600 text-white px-4 py-2 rounded"
                          >
                            Save
                          </button>
                          <button
                            type="button"
                            className="bg-gray-300 px-4 py-2 rounded"
                            onClick={() => setEditingId(null)}
                          >
                            Cancel
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListingDashboard;
