import React, { useState } from "react";

const Upload = () => {
  const [form, setForm] = useState({
    title: "",
    description: "",
    agent_name: "",
    agent_contact: "",
    price: "",
    location: "",
    types: "",
    beds:"",
    sqft:"",
    bathrooms:"",
    images: [], // For storing selected files
  });

  const handleUpload = async (e) => {
    e.preventDefault();

    const formDataObj = new FormData();
    formDataObj.append("title", form.title);
    formDataObj.append("description", form.description);
    formDataObj.append("price", form.price);
    formDataObj.append("agent_contact", form.agent_contact);
    formDataObj.append("location", form.location);
    formDataObj.append("types", form.types);
    formDataObj.append("types", form.beds);
    formDataObj.append("types", form.bathrooms);
    formDataObj.append("types", form.types);

    // Append each image
    if (!form.images || form.images.length === 0) {
      alert("Please select at least one image.");
      return;
    }

    for (let i = 0; i < form.images.length; i++) {
      formDataObj.append("images", form.images[i]);
    }
    const res = await fetch("/api/admin/listings", {
      method: "POST",
      body: formDataObj,
    });
    setForm({ title: "", description: "", agent_name: "", agent_contact: "", price: "", bed: "", bathroom: "", sqft: ""});

    if (res.ok) {
      alert("Listings Uploaded Sucessfully");
    } else {
      alert("Failed to upload");
    }
  };

  return (
    <div className="flex h-screen bg-blue-500 w-full justify-center items-center ">
      <div className="overflow-auto w-full h-screen">
        <div className="bg-white my-20 p-5 shadow-md rounded-md">
          <form
            onSubmit={handleUpload}
            action=""
            className="flex flex-col space-y-7"
          >
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Property Name:</label>
              <input
                type="text"
                value={form.title}
                placeholder="Property name..."
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, title: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Price:</label>
              <input
                type="number"
                value={form.price}
                placeholder="Price"
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, price: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 relative ">
              <label>Images:</label>
              <input
                type="file"
                multiple
                className=" absolute px-12 rounded-lg py-3 bg-gray-300 
             cursor-pointer inset-0 opacity-0 "
                onChange={(e) =>
                  setForm({ ...form, images: Array.from(e.target.files) })
                }
              />
              <button className="py-9 px-3 rounded-md bg-gray-300">
                Upload Images
              </button>
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Description:</label>
              <textarea
                value={form.description}
                placeholder="Property details..."
                className="px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, description: e.target.value });
                }}
              ></textarea>
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Location:</label>
              <input
                type="text"
                value={form.location}
                placeholder="Property location..."
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, location: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Property Types:</label>
              <select
                className=" px-12 rounded-lg py-3 bg-gray-300"
                value={form.types}
                onChange={(e) => {
                  setForm({ ...form, types: e.target.value });
                }}
              >
                <option value="Condo">Condo</option>
                <option value="Condo">Condo</option>
                <option value="Condo">Condo</option>
                <option value="Condo">Condo</option>
              </select>
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Agent Email:</label>
              <input
                value={form.agent_contact}
                placeholder="Agent email address..."
                type="email"
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, agent_contact: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Beds:</label>
              <input
                value={form.beds}
                placeholder="Num of Beds"
                type="number"
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, beds: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Sqft:</label>
              <input
                value={form.sqft}
                placeholder="Square Foot"
                type="number"
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, sqft: e.target.value });
                }}
              />
            </div>
            <div className="grid grid-cols-3 items-center gap-4 ">
              <label>Baths:</label>
              <input
                value={form.baths}
                placeholder="Num of Bathrooms"
                type="number"
                className=" px-12 rounded-lg py-3 bg-gray-300"
                onChange={(e) => {
                  setForm({ ...form, baths: e.target.value });
                }}
              />
            </div>
            <button className="place-self-start py-3 px-6 bg-blue-600 rounded-lg text-white hover:bg-blue-700 hover:text-black transition-colors duration-200ms">
              Upload Listing
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Upload;
