import React, { useEffect, useState } from "react";
import { FaRuler, FaBed, FaBath, FaHeart } from "react-icons/fa";

const optimizeCloudinaryUrl = (url) => {
  if (!url || !url.includes("res.cloudinary.com")) return url;
  return url.replace("/upload/", "/upload/f_auto,q_auto,w_800/");
};

const FeaturedProperty = ({ setSelectedProperty, searchQuery }) => {
  const [listings, setListings] = useState([]);
  const [filteredListings, setFilteredListings] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch listings once
  const fetchListing = () => {
    setLoading(true);
    fetch("/api/displaylistings")
      .then((res) => res.json())
      .then((data) => {
        setListings(data);
        setFilteredListings(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchListing();
  }, []);

  // Filter when search changes
  useEffect(() => {
    if (!listings.length) return;

    setLoading(true);
    const delay = setTimeout(() => {
      if (!searchQuery) {
        setFilteredListings(listings);
      } else {
        const lower = searchQuery.toLowerCase();
        setFilteredListings(
          listings.filter(
            (p) =>
              p.title?.toLowerCase().includes(lower) ||
              p.location?.toLowerCase().includes(lower) ||
              String(p.price).includes(lower)
          )
        );
      }
      setLoading(false);
    }, 500); // half-second delay so loader is visible

    return () => clearTimeout(delay);
  }, [searchQuery, listings]);

  return (
    <section className="max-w-7xl mx-auto py-16 px-4 relative">
      <h2 className="text-3xl font-bold mb-8">Featured Properties</h2>

      {/* Loader */}
      {loading && (
        <div className="absolute inset-0 flex justify-center items-center bg-white/70 z-10">
          <div className="flex items-center gap-3 bg-white p-4 rounded-lg shadow">
            <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-blue-600"></div>
            <span className="text-gray-700 font-medium">Loading...</span>
          </div>
        </div>
      )}

      {/* Listings */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredListings.map((property) => {
          const previewImage = optimizeCloudinaryUrl(
            property.image_urls?.[0] || ""
          );
          return (
            <div
              key={property.id}
              onClick={() => setSelectedProperty(property)}
              className="bg-white rounded-2xl drop-shadow-lg overflow-hidden hover:drop-shadow-xl hover:scale-105 transition-all duration-300 relative group cursor-pointer"
            >
              <div className="relative">
                <img
                  src={previewImage}
                  alt={property.title}
                  className="w-full h-72 object-cover group-hover:scale-105 transition-transform duration-300"
                  loading="lazy"
                />
                <button className="absolute top-4 right-4 p-2 rounded-full bg-white/70 hover:bg-white transition-colors duration-200">
                  <FaHeart />
                </button>
              </div>

              <div className="p-6">
                <div className="flex items-center gap-2 text-gray-500 text-sm mb-3">
                  <span>{property.location}</span>
                </div>
                <div className="text-2xl font-bold text-blue-600 mb-2">
                  ${property.price}
                </div>

                <h1 className="text-xl font-semibold mb-4 text-gray-800">
                  {property.title}
                </h1>

                <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg">
                  <div className="flex items-center gap-2">
                    <FaBed className="text-blue-600" />
                    <span className="text-gray-600">{property.beds}</span>
                  </div>

                  <div className="flex items-center gap-2">
                    <FaBath className="text-blue-600" />
                    <span className="text-gray-600">{property.baths}</span>
                  </div>

                  <div className="flex items-center gap-2">
                    <FaRuler className="text-blue-600" />
                    <span className="text-gray-600">{property.sqft}</span>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {!loading && filteredListings.length === 0 && (
        <p className="mt-6 text-gray-500">No properties found.</p>
      )}
    </section>
  );
};

export default FeaturedProperty;
